        /**
         * Pulsating animation for the map pin
        */
        const pulsate = (node, { delay=0, duration=1000, easing=t => t}) => {
            return {
                duration,
                delay,
                css: t => {
                    let scale = 1 + 0.1 * easing(t);
                    const max_opacity_point = 0.5;
                    let opacity = Math.abs(max_opacity_point - t);

                    return `
                        transform: scale(${scale}, ${scale});
                        opacity: ${opacity};
                    `;
                }
            }
        }
  
  /*=====  End of Methods  ======*/
  
  
  
  
